function [prob predValue] = predict(string, theta, vocabList, map)  predValue = -1;  input = strsplit(string, " ");  len = size(input,2);  n = size(vocabList, 1);  featureVector = zeros(1, n);  map  for i = 1:len    for j = 1:n      if(strcmp(input{i}, vocabList{j}) ==1)          featureVector(1, j) = 1;        endif      endfor    endfor    featureVector = [1 featureVector];    prob = sigmoid(featureVector, theta);    if(prob >= 0.5)      predValue = 0;    else      predValue = 1;    endif    fprintf('Predicted that "%s" corresponds to "%s" classification with probability %f.\n', ...      string, map{predValue+1}, prob);
endfunction
